import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import {
  getAuth,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut,
  onAuthStateChanged,
} from 'firebase/auth';
import {
  getFirestore,
  collection,
  onSnapshot,
  doc,
  addDoc,
  deleteDoc,
  serverTimestamp,
} from 'firebase/firestore';

// Your personal Firebase configuration has been placed here.
const firebaseConfig = {
  apiKey: "AIzaSyB364oqoNGpGs1KHQhFY75KsGy2gQwYjUI",
  authDomain: "fitness-tracker-ff53e.firebaseapp.com",
  projectId: "fitness-tracker-ff53e",
  storageBucket: "fitness-tracker-ff53e.firebasestorage.app",
  messagingSenderId: "608373111852",
  appId: "1:608373111852:web:af0dfb9dcc685947c2a589"
};

const App = () => {
  // Define app ID from the Canvas environment
  const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
  
  // State variables for Firebase services and user data
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // State for fitness tracking data
  const [activity, setActivity] = useState('');
  const [duration, setDuration] = useState('');
  const [entries, setEntries] = useState([]);

  // State for authentication UI
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoginMode, setIsLoginMode] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');

  // Initialize Firebase and set up authentication listener
  useEffect(() => {
    try {
      if (Object.keys(firebaseConfig).length === 0) {
        throw new Error("Firebase config is missing from the code.");
      }
      const app = initializeApp(firebaseConfig);
      const firestoreDb = getFirestore(app);
      const firebaseAuth = getAuth(app);
      setDb(firestoreDb);
      setAuth(firebaseAuth);

      const unsubscribe = onAuthStateChanged(firebaseAuth, (currentUser) => {
        setUser(currentUser);
        setIsLoading(false);
      });

      return () => unsubscribe();
    } catch (error) {
      console.error("Error initializing Firebase:", error);
      setIsLoading(false);
      setErrorMessage("Failed to initialize the app. Please ensure your Firebase config is correct and the required services are enabled.");
    }
  }, []);

  // Handle Firebase authentication logic (sign up, sign in)
  const handleAuth = async (e) => {
    e.preventDefault();
    setErrorMessage('');
    if (!auth) {
      setErrorMessage('Authentication service not initialized.');
      return;
    }
    if (!email || !password) {
      setErrorMessage('Email and password cannot be empty.');
      return;
    }
    try {
      if (isLoginMode) {
        await signInWithEmailAndPassword(auth, email, password);
      } else {
        await createUserWithEmailAndPassword(auth, email, password);
      }
    } catch (error) {
      console.error('Authentication Error:', error);
      let message = 'An error occurred during authentication.';
      if (error.code === 'auth/email-already-in-use') {
        message = 'This email is already in use.';
      } else if (error.code === 'auth/invalid-email') {
        message = 'The email address is not valid.';
      } else if (error.code === 'auth/weak-password') {
        message = 'Password should be at least 6 characters.';
      } else if (error.code === 'auth/wrong-password' || error.code === 'auth/user-not-found') {
        message = 'Invalid email or password.';
      }
      setErrorMessage(message);
    }
  };

  // Handle user sign out
  const handleSignOut = async () => {
    try {
      await signOut(auth);
      setEntries([]); // Clear entries on sign-out
    } catch (error) {
      console.error("Error signing out:", error);
    }
  };

  // Fetch data from Firestore once the user is authenticated
  useEffect(() => {
    if (!db || !user) {
      setEntries([]);
      return;
    }
    
    // Using the simplified collection path
    const userFitnessCollectionRef = collection(db, `artifacts/${appId}/users/${user.uid}/fitness-data`);

    const unsubscribe = onSnapshot(userFitnessCollectionRef, (snapshot) => {
      const fetchedEntries = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      const sortedEntries = fetchedEntries.sort((a, b) => {
        const aTime = a.timestamp?.seconds || 0;
        const bTime = b.timestamp?.seconds || 0;
        return bTime - aTime;
      });
      setEntries(sortedEntries);
    }, (error) => {
      console.error("Error fetching documents:", error);
    });

    return () => unsubscribe();
  }, [db, user, appId]);

  // Handle form submission to add a new fitness entry
  const handleAddEntry = async (e) => {
    e.preventDefault();
    if (!db || !user || !activity || !duration) {
      return;
    }
    const userFitnessCollectionRef = collection(db, `artifacts/${appId}/users/${user.uid}/fitness-data`);
    try {
      await addDoc(userFitnessCollectionRef, {
        activity,
        duration: Number(duration),
        timestamp: serverTimestamp(),
        userId: user.uid,
      });
      setActivity('');
      setDuration('');
    } catch (error) {
      console.error("Error adding document:", error);
    }
  };

  // Handle deleting a fitness entry
  const handleDeleteEntry = async (entryId) => {
    if (!db || !user) {
      return;
    }
    try {
      const docRef = doc(db, `artifacts/${appId}/users/${user.uid}/fitness-data`, entryId);
      await deleteDoc(docRef);
    } catch (error) {
      console.error("Error deleting document:", error);
    }
  };

  // Loading state UI
  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-900 text-gray-200">
        <p>Loading...</p>
      </div>
    );
  }

  // Handle errors during Firebase initialization
  if (errorMessage) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-900 text-red-400 p-4 text-center">
        <p>{errorMessage}</p>
      </div>
    );
  }

  // Authentication UI
  if (!user) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-900 text-gray-200 p-4">
        <div className="bg-gray-800 p-8 rounded-xl shadow-lg w-full max-w-md">
          <h1 className="text-3xl font-bold text-center mb-6 text-indigo-400">Fitness Tracker</h1>
          <h2 className="text-xl font-semibold text-center mb-4">{isLoginMode ? 'Sign In' : 'Sign Up'}</h2>
          <form onSubmit={handleAuth}>
            {errorMessage && (
              <p className="text-red-400 text-center mb-4">{errorMessage}</p>
            )}
            <div className="mb-4">
              <label className="block text-sm font-medium mb-1" htmlFor="email">
                Email
              </label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full px-4 py-2 bg-gray-700 text-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="you@example.com"
                required
              />
            </div>
            <div className="mb-6">
              <label className="block text-sm font-medium mb-1" htmlFor="password">
                Password
              </label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-2 bg-gray-700 text-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="••••••••"
                required
              />
            </div>
            <button
              type="submit"
              className="w-full bg-indigo-600 text-white font-bold py-2 px-4 rounded-lg hover:bg-indigo-700 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 focus:ring-offset-gray-900"
            >
              {isLoginMode ? 'Sign In' : 'Sign Up'}
            </button>
          </form>
          <div className="mt-4 text-center">
            <button
              onClick={() => setIsLoginMode(!isLoginMode)}
              className="text-indigo-400 hover:text-indigo-300 transition-colors text-sm"
            >
              {isLoginMode ? "Don't have an account? Sign up" : "Already have an account? Sign in"}
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Main application UI after successful login
  return (
    <div className="min-h-screen bg-gray-900 text-gray-200 p-4 font-sans">
      <div className="container mx-auto max-w-4xl">
        {/* Header with user ID and sign out button */}
        <header className="flex flex-col sm:flex-row items-center justify-between mb-8 p-4 bg-gray-800 rounded-xl shadow-lg">
          <h1 className="text-4xl font-extrabold text-indigo-400 mb-2 sm:mb-0">My Fitness Tracker</h1>
          <div className="flex items-center space-x-4">
            <span className="text-sm font-medium text-gray-400 truncate">
              User ID: {user.uid}
            </span>
            <button
              onClick={handleSignOut}
              className="bg-red-600 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded-lg transition-colors focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2 focus:ring-offset-gray-900"
            >
              Sign Out
            </button>
          </div>
        </header>

        {/* Add new entry form */}
        <section className="bg-gray-800 p-6 rounded-xl shadow-lg mb-8">
          <h2 className="text-2xl font-bold text-gray-200 mb-4">Add a New Activity</h2>
          <form onSubmit={handleAddEntry} className="space-y-4">
            <div>
              <label htmlFor="activity" className="block text-sm font-medium text-gray-400 mb-1">
                Activity
              </label>
              <input
                type="text"
                id="activity"
                value={activity}
                onChange={(e) => setActivity(e.target.value)}
                className="w-full px-4 py-2 bg-gray-700 text-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="e.g., Running, Weightlifting, Yoga"
                required
              />
            </div>
            <div>
              <label htmlFor="duration" className="block text-sm font-medium text-gray-400 mb-1">
                Duration (minutes)
              </label>
              <input
                type="number"
                id="duration"
                value={duration}
                onChange={(e) => setDuration(e.target.value)}
                className="w-full px-4 py-2 bg-gray-700 text-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="e.g., 30, 45, 60"
                required
              />
            </div>
            <button
              type="submit"
              className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 rounded-lg transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 focus:ring-offset-gray-900"
            >
              Log Activity
            </button>
          </form>
        </section>

        {/* Fitness entries list */}
        <section className="bg-gray-800 p-6 rounded-xl shadow-lg">
          <h2 className="text-2xl font-bold text-gray-200 mb-4">Your Recent Activities</h2>
          {entries.length === 0 ? (
            <p className="text-center text-gray-500 py-8">No activities logged yet.</p>
          ) : (
            <ul className="space-y-4">
              {entries.map((entry) => (
                <li
                  key={entry.id}
                  className="flex items-center justify-between bg-gray-700 p-4 rounded-lg shadow"
                >
                  <div>
                    <p className="font-semibold text-lg text-gray-100">
                      {entry.activity}
                    </p>
                    <p className="text-sm text-gray-400">
                      {entry.duration} min
                    </p>
                  </div>
                  <button
                    onClick={() => handleDeleteEntry(entry.id)}
                    className="p-2 text-red-400 hover:text-red-500 transition-colors"
                    aria-label="Delete activity"
                  >
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      className="h-5 w-5"
                      viewBox="0 0 20 20"
                      fill="currentColor"
                    >
                      <path
                        fillRule="evenodd"
                        d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm6 0a1 1 0 11-2 0v6a1 1 0 112 0V8z"
                        clipRule="evenodd"
                      />
                    </svg>
                  </button>
                </li>
              ))}
            </ul>
          )}
        </section>
      </div>
    </div>
  );
};

export default App;
