import React, { useState, useEffect } from 'react';
import {
  format,
  addMonths,
  subMonths,
  startOfWeek,
  addDays,
  startOfMonth,
  endOfMonth,
  endOfWeek,
  isSameMonth,
  isSameDay,
  parse,
} from 'date-fns';
import { initializeApp } from 'firebase/app';
import {
  getAuth,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  signOut,
  onAuthStateChanged,
} from 'firebase/auth';
import {
  getFirestore,
  doc,
  collection,
  onSnapshot,
  setDoc,
  deleteDoc,
  query,
} from 'firebase/firestore';

// The Firebase configuration provided by the user
const firebaseConfig = {
  apiKey: "AIzaSyB364oqoNGpGs1KHQhFY75KsGy2gQwYjUI",
  authDomain: "fitness-tracker-ff53e.firebaseapp.com",
  projectId: "fitness-tracker-ff53e",
  storageBucket: "fitness-tracker-ff53e.firebasestorage.app",
  messagingSenderId: "608373111852",
  appId: "1:608373111852:web:af0dfb9dcc685947c2a589"
};

// Initialize Firebase App
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);

const App = () => {
  // Authentication states
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [authError, setAuthError] = useState('');
  const [isLoginView, setIsLoginView] = useState(true);

  // App states
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [view, setView] = useState('calendar');
  const [activity, setActivity] = useState('');
  const [duration, setDuration] = useState('');
  const [activities, setActivities] = useState([]);
  const [schedule, setSchedule] = useState([]);
  const [scheduledEvent, setScheduledEvent] = useState('');
  const [scheduledTime, setScheduledTime] = useState('');

  // Use the global __app_id variable for Firestore paths
  const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';

  // --- Firebase Authentication Listener & Data Fetching ---
  useEffect(() => {
    // Listen for authentication state changes
    const unsubscribeAuth = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      if (currentUser) {
        setAuthError('');
      } else {
        // Clear data when user logs out
        setActivities([]);
        setSchedule([]);
      }
    });

    return () => unsubscribeAuth();
  }, [auth]);

  // Listen for real-time Firestore data changes for the authenticated user
  useEffect(() => {
    // Only fetch data if a user is logged in
    if (user) {
      const userId = user.uid;
      const userActivitiesCollectionRef = collection(db, 'artifacts', appId, 'users', userId, 'activities');
      const userScheduleCollectionRef = collection(db, 'artifacts', appId, 'users', userId, 'schedule');

      // Set up real-time listener for activities
      const unsubscribeActivities = onSnapshot(userActivitiesCollectionRef, (snapshot) => {
        const newActivities = snapshot.docs.map(doc => ({
          ...doc.data(),
          id: doc.id,
        }));
        setActivities(newActivities);
      }, (error) => {
        console.error('Failed to fetch activities:', error);
      });

      // Set up real-time listener for schedule
      const unsubscribeSchedule = onSnapshot(userScheduleCollectionRef, (snapshot) => {
        const newSchedule = snapshot.docs.map(doc => ({
          ...doc.data(),
          id: doc.id,
        }));
        setSchedule(newSchedule);
      }, (error) => {
        console.error('Failed to fetch schedule:', error);
      });

      // Clean up the listeners when the component unmounts or the user logs out
      return () => {
        unsubscribeActivities();
        unsubscribeSchedule();
      };
    }
  }, [user, db, appId]);


  // --- Authentication Handlers ---
  const handleAuthAction = async (e) => {
    e.preventDefault();
    setAuthError('');
    try {
      if (isLoginView) {
        await signInWithEmailAndPassword(auth, email, password);
      } else {
        await createUserWithEmailAndPassword(auth, email, password);
      }
    } catch (error) {
      setAuthError(error.message);
      console.error('Authentication Error:', error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
    } catch (error) {
      console.error('Logout Error:', error);
    }
  };


  // --- UI Render Functions ---
  const renderHeader = () => {
    return (
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
        <button onClick={prevMonth}>{'<'}</button>
        <div style={{ fontWeight: 'bold' }}>
          <span>{format(currentMonth, 'MMMM yyyy')}</span>
        </div>
        <button onClick={nextMonth}>{'>'}</button>
      </div>
    );
  };

  const renderDays = () => {
    const dateFormat = 'EEEE';
    const days = [];
    let startDate = startOfWeek(currentMonth);

    for (let i = 0; i < 7; i++) {
      days.push(
        <div key={i} style={{ textAlign: 'center', padding: '0.5rem' }}>
          {format(addDays(startDate, i), dateFormat)}
        </div>
      );
    }
    return <div style={{ display: 'grid', gridTemplateColumns: 'repeat(7, 1fr)', marginBottom: '0.5rem' }}>{days}</div>;
  };

  const renderCells = () => {
    const monthStart = startOfMonth(currentMonth);
    const monthEnd = endOfMonth(monthStart);
    const startDate = startOfWeek(monthStart);
    const endDate = endOfWeek(monthEnd);

    const dateFormat = 'd';
    const rows = [];
    let days = [];
    let day = startDate;
    let formattedDate = '';

    while (day <= endDate) {
      for (let i = 0; i < 7; i++) {
        formattedDate = format(day, dateFormat);
        const cloneDay = day;

        const dayActivities = activities.filter(
          (act) => isSameDay(parse(act.date, 'yyyy-MM-dd', new Date()), cloneDay)
        );

        const daySchedule = schedule.filter(
          (event) => isSameDay(parse(event.date, 'yyyy-MM-dd', new Date()), cloneDay)
        );

        const hasEvents = dayActivities.length > 0 || daySchedule.length > 0;

        days.push(
          <div
            style={{
              padding: '1rem',
              textAlign: 'center',
              border: '1px solid #ccc',
              backgroundColor: isSameDay(day, selectedDate) ? 'lightblue' : '#f0f0f0',
              opacity: isSameMonth(day, monthStart) ? 1 : 0.5,
              cursor: 'pointer',
              position: 'relative'
            }}
            key={day}
            onClick={() => onDateClick(cloneDay)}
          >
            <span>{formattedDate}</span>
            {hasEvents && (
              <span style={{ position: 'absolute', top: '5px', right: '5px', width: '8px', height: '8px', backgroundColor: 'green', borderRadius: '50%' }}></span>
            )}
          </div>
        );
        day = addDays(day, 1);
      }
      rows.push(
        <div key={day} style={{ display: 'grid', gridTemplateColumns: 'repeat(7, 1fr)' }}>
          {days}
        </div>
      );
      days = [];
    }
    return <div>{rows}</div>;
  };

  const onDateClick = (day) => {
    setSelectedDate(day);
    setView('calendar');
  };

  const nextMonth = () => {
    setCurrentMonth(addMonths(currentMonth, 1));
  };

  const prevMonth = () => {
    setCurrentMonth(subMonths(currentMonth, 1));
  };

  // --- Firestore Data Handlers ---
  const handleAddActivity = async (e) => {
    e.preventDefault();
    if (!user || !activity || !duration) return;

    const newActivity = {
      activity,
      duration: Number(duration),
      date: format(selectedDate, 'yyyy-MM-dd'),
    };
    try {
      // Add a new document to the user's activities subcollection
      const activitiesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'activities');
      await setDoc(doc(activitiesCollectionRef), newActivity);
      setActivity('');
      setDuration('');
    } catch (e) {
      console.error('Error adding document: ', e);
    }
  };

  const handleDeleteActivity = async (id) => {
    if (!user) return;
    try {
      // Delete a document from the user's activities subcollection
      const activityDocRef = doc(db, 'artifacts', appId, 'users', user.uid, 'activities', id);
      await deleteDoc(activityDocRef);
    } catch (e) {
      console.error('Error deleting document: ', e);
    }
  };

  const handleAddSchedule = async (e) => {
    e.preventDefault();
    if (!user || !scheduledEvent || !scheduledTime) return;

    const newSchedule = {
      event: scheduledEvent,
      time: scheduledTime,
      date: format(selectedDate, 'yyyy-MM-dd'),
    };
    try {
      // Add a new document to the user's schedule subcollection
      const scheduleCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'schedule');
      await setDoc(doc(scheduleCollectionRef), newSchedule);
      setScheduledEvent('');
      setScheduledTime('');
    } catch (e) {
      console.error('Error adding schedule: ', e);
    }
  };

  const handleDeleteSchedule = async (id) => {
    if (!user) return;
    try {
      // Delete a document from the user's schedule subcollection
      const scheduleDocRef = doc(db, 'artifacts', appId, 'users', user.uid, 'schedule', id);
      await deleteDoc(scheduleDocRef);
    } catch (e) {
      console.error('Error deleting schedule: ', e);
    }
  };


  const renderSelectedDateInfo = () => {
    const activitiesForDate = activities
      .filter((act) => isSameDay(parse(act.date, 'yyyy-MM-dd', new Date()), selectedDate))
      .sort((a, b) => b.id - a.id);

    const scheduleForDate = schedule
      .filter((s) => isSameDay(parse(s.date, 'yyyy-MM-dd', new Date()), selectedDate))
      .sort((a, b) => a.time.localeCompare(b.time));

    return (
      <div style={{ marginTop: '2rem' }}>
        <h3>Activities for {format(selectedDate, 'EEEE, MMMM do')}</h3>
        <form onSubmit={handleAddActivity}>
          <input
            type="text"
            value={activity}
            onChange={(e) => setActivity(e.target.value)}
            placeholder="Activity"
            required
          />
          <input
            type="number"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
            placeholder="Duration (min)"
            required
          />
          <button type="submit">Log Activity</button>
        </form>
        <div>
          <h4>Logged Activities</h4>
          {activitiesForDate.length === 0 ? (
            <p>No activities logged for this day.</p>
          ) : (
            <ul>
              {activitiesForDate.map((act) => (
                <li key={act.id}>
                  {act.activity} ({act.duration} min)
                  <button onClick={() => handleDeleteActivity(act.id)}>Delete</button>
                </li>
              ))}
            </ul>
          )}
        </div>
        <div>
          <h4>Scheduled Events</h4>
          <form onSubmit={handleAddSchedule}>
            <input
              type="text"
              value={scheduledEvent}
              onChange={(e) => setScheduledEvent(e.target.value)}
              placeholder="Event"
              required
            />
            <input
              type="time"
              value={scheduledTime}
              onChange={(e) => setScheduledTime(e.target.value)}
              required
            />
            <button type="submit">Add to Schedule</button>
          </form>
          {scheduleForDate.length === 0 ? (
            <p>No events scheduled for this day.</p>
          ) : (
            <ul>
              {scheduleForDate.map((s) => (
                <li key={s.id}>
                  {s.time} - {s.event}
                  <button onClick={() => handleDeleteSchedule(s.id)}>Delete</button>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    );
  };

  const renderHistory = () => {
    const sortedActivities = [...activities].sort((a, b) => {
      const dateA = new Date(a.date);
      const dateB = new Date(b.date);
      return dateB - dateA;
    });

    return (
      <div style={{ marginTop: '2rem' }}>
        <h3>Activity History</h3>
        {sortedActivities.length === 0 ? (
          <p>No activities logged yet.</p>
        ) : (
          <ul>
            {sortedActivities.map((act) => (
              <li key={act.id}>
                <strong>{act.activity}</strong> on {format(parse(act.date, 'yyyy-MM-dd', new Date()), 'MMMM do, yyyy')} for {act.duration} min
                <button onClick={() => handleDeleteActivity(act.id)}>Delete</button>
              </li>
            ))}
          </ul>
        )}
      </div>
    );
  };

  // --- Main App Render ---
  return (
    <div style={{ padding: '1rem', fontFamily: 'sans-serif' }}>
      <header style={{ marginBottom: '2rem' }}>
        <h1 style={{ marginBottom: '0.5rem' }}>My Fitness & Life Tracker</h1>
        {user ? (
          <div>
            <span style={{ marginRight: '1rem' }}>Logged in as: <strong>{user.email}</strong></span>
            <button onClick={handleLogout}>Logout</button>
          </div>
        ) : (
          <div>
            <form onSubmit={handleAuthAction}>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Email"
                required
                style={{ marginRight: '0.5rem' }}
              />
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Password"
                required
                style={{ marginRight: '0.5rem' }}
              />
              <button type="submit">{isLoginView ? 'Login' : 'Register'}</button>
            </form>
            <p style={{ color: 'red', marginTop: '0.5rem' }}>{authError}</p>
            <button onClick={() => setIsLoginView(!isLoginView)} style={{ marginTop: '0.5rem' }}>
              {isLoginView ? 'Need to register?' : 'Already have an account?'}
            </button>
          </div>
        )}
      </header>
      {user && (
        <>
          <div>
            <button onClick={() => setView('calendar')} style={{ marginRight: '10px' }}>
              Calendar
            </button>
            <button onClick={() => setView('history')}>
              History
            </button>
          </div>
          {view === 'calendar' && (
            <>
              {renderHeader()}
              {renderDays()}
              {renderCells()}
              {renderSelectedDateInfo()}
            </>
          )}
          {view === 'history' && renderHistory()}
        </>
      )}
    </div>
  );
};

export default App;
