import { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, signInAnonymously, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, doc, setDoc, onSnapshot, collection, query, where, getDocs } from 'firebase/firestore';

// Main App component
export default function App() {
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [userId, setUserId] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [appData, setAppData] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const [message, setMessage] = useState('');

  // Initial Firebase setup and authentication check
  useEffect(() => {
    try {
      // Accessing global variables for Firebase config and auth token
      const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : null;
      const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;
      const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';

      if (!firebaseConfig) {
        throw new Error("Firebase config is not available.");
      }

      // Initialize Firebase services
      const app = initializeApp(firebaseConfig);
      const firestoreDb = getFirestore(app);
      const firebaseAuth = getAuth(app);
      setDb(firestoreDb);
      setAuth(firebaseAuth);

      // Listen for auth state changes and handle sign-in
      const unsubscribe = onAuthStateChanged(firebaseAuth, async (user) => {
        if (user) {
          setUserId(user.uid);
          setIsAuthReady(true);
        } else {
          // If no user is signed in, handle the initial sign-in
          try {
            if (initialAuthToken) {
              await signInWithCustomToken(firebaseAuth, initialAuthToken);
            } else {
              await signInAnonymously(firebaseAuth);
            }
          } catch (error) {
            console.error("Error signing in:", error);
            setMessage(`Error signing in: ${error.message}`);
          }
        }
        setIsLoading(false);
      });

      return () => unsubscribe();
    } catch (error) {
      console.error("Firebase Initialization Error:", error);
      setMessage(`Initialization Error: ${error.message}`);
      setIsLoading(false);
    }
  }, []);

  // Real-time data listener for the user's document
  useEffect(() => {
    // Only subscribe if Firebase is ready and user is authenticated
    if (!isAuthReady || !db || !userId) return;

    // Define the path to the user's private data
    const docRef = doc(db, `artifacts/${typeof __app_id !== 'undefined' ? __app_id : 'default-app-id'}/users/${userId}/data/user_data`);

    // Listen for real-time updates to the document
    const unsubscribe = onSnapshot(docRef, (docSnap) => {
      if (docSnap.exists()) {
        setAppData(docSnap.data());
      } else {
        setAppData({});
      }
      setIsLoading(false);
    }, (error) => {
      console.error("Error fetching document:", error);
      setMessage(`Error fetching data: ${error.message}`);
      setIsLoading(false);
    });

    return () => unsubscribe();
  }, [isAuthReady, db, userId]);

  // Function to save or update data in Firestore
  const saveOrUpdateData = async (newData) => {
    if (!db || !userId) {
      setMessage("Please wait, authentication is not complete.");
      return;
    }

    setIsLoading(true);
    const docRef = doc(db, `artifacts/${typeof __app_id !== 'undefined' ? __app_id : 'default-app-id'}/users/${userId}/data/user_data`);
    try {
      await setDoc(docRef, newData, { merge: true });
      setMessage("Data saved successfully!");
    } catch (e) {
      console.error("Error adding/updating document: ", e);
      setMessage(`Error saving data: ${e.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  // UI rendering based on state
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4 font-sans">
      <div className="w-full max-w-2xl bg-white rounded-xl shadow-lg p-6 space-y-6 text-center">
        <h1 className="text-3xl font-bold text-gray-800">My Firebase App</h1>
        <p className="text-gray-600">
          This is a user-specific application. Your data is stored privately.
        </p>

        {isLoading ? (
          <div className="flex items-center justify-center space-x-2">
            <div className="w-4 h-4 rounded-full bg-blue-500 animate-bounce" style={{ animationDelay: '-0.3s' }}></div>
            <div className="w-4 h-4 rounded-full bg-blue-500 animate-bounce" style={{ animationDelay: '-0.15s' }}></div>
            <div className="w-4 h-4 rounded-full bg-blue-500 animate-bounce"></div>
            <span className="text-blue-500 font-medium ml-2">Loading...</span>
          </div>
        ) : (
          <div className="space-y-4">
            <div className="bg-blue-100 p-4 rounded-lg">
              <p className="text-blue-800 font-semibold">User ID:</p>
              <p className="text-blue-600 break-all">{userId || 'Not signed in'}</p>
            </div>

            {message && (
              <p className={`p-3 rounded-lg text-sm font-medium ${message.startsWith('Error') ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>
                {message}
              </p>
            )}

            <div className="space-y-2">
              <label htmlFor="name" className="text-gray-700 block text-left font-medium">Your Name:</label>
              <input
                type="text"
                id="name"
                value={appData.name || ''}
                onChange={(e) => setAppData({ ...appData, name: e.target.value })}
                className="w-full p-3 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter your name"
              />
            </div>
            
            <div className="space-y-2">
              <label htmlFor="bio" className="text-gray-700 block text-left font-medium">Your Bio:</label>
              <textarea
                id="bio"
                rows="4"
                value={appData.bio || ''}
                onChange={(e) => setAppData({ ...appData, bio: e.target.value })}
                className="w-full p-3 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Tell us a little about yourself"
              ></textarea>
            </div>
            
            <button
              onClick={() => saveOrUpdateData(appData)}
              className="w-full bg-blue-500 text-white font-semibold py-3 px-4 rounded-md transition duration-300 ease-in-out hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
              disabled={isLoading}
            >
              {isLoading ? 'Saving...' : 'Save Data'}
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
