import { useState } from 'react';

// The main App component containing the updated Login form
export default function App() {
  // State to manage the user's email and password
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  // Function to handle the form submission
  const handleLogin = (e) => {
    e.preventDefault(); // Prevent the default form submission behavior

    // Simple validation
    if (email === '' || password === '') {
      setMessage("Please enter both an email and a password.");
      return;
    }
    
    // In a real application, you would send this data to a backend for authentication
    // For now, we'll just log it to the console and show a success message
    console.log('Attempting login with:', { email, password });
    setMessage("Login attempt successful! (Note: This is a placeholder action)");
    
    // You could also clear the form fields after submission if needed
    // setEmail('');
    // setPassword('');
  };

  return (
    <div className="flex min-h-screen items-center justify-center bg-gray-100 p-4 font-sans">
      <div className="w-full max-w-sm rounded-xl bg-white p-6 shadow-xl ring-1 ring-gray-200">
        <h1 className="text-center text-3xl font-bold text-gray-800">Login</h1>

        <form onSubmit={handleLogin} className="mt-8 space-y-4">
          <div>
            <label className="mb-1 block text-sm font-medium text-gray-700" htmlFor="email">
              Email
            </label>
            <input
              id="email"
              type="email"
              placeholder="you@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full rounded-lg border border-gray-300 p-2 text-gray-900 focus:border-indigo-500 focus:ring-indigo-500"
              required
            />
          </div>

          <div>
            <label className="mb-1 block text-sm font-medium text-gray-700" htmlFor="password">
              Password
            </label>
            <input
              id="password"
              type="password"
              placeholder="••••••••"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full rounded-lg border border-gray-300 p-2 text-gray-900 focus:border-indigo-500 focus:ring-indigo-500"
              required
            />
          </div>

          {/* Display a message to the user */}
          {message && (
            <div className="text-sm font-medium text-gray-600">
              {message}
            </div>
          )}

          <button
            type="submit"
            className="w-full rounded-lg bg-indigo-600 py-2 text-lg font-semibold text-white transition-colors hover:bg-indigo-700"
          >
            Login
          </button>
        </form>

        <p className="mt-6 text-center text-sm text-gray-500">
          Don't have an account?{' '}
          <a href="#" className="font-semibold text-indigo-600 hover:text-indigo-500">
            Sign Up
          </a>
        </p>
      </div>
    </div>
  );
}
